import React,{useReducer,useContext} from 'react'
import { AppContext } from './context';
function Card(props) {
 
  function addItem(id, list) {
    var i;
    for (i = 0; i < list.length; i++) {
        if (list[i].id === id) {
            
            return true;
        }
    }
    
    return false;
}
  function handleClick(event)
  {
    console.log("printing in cards");
    const storedItems = JSON.parse(localStorage.getItem('items'))

    let object={id:event.target.value,count:1}

    if(addItem(event.target.value,storedItems)===false)
    {
        storedItems.push(object);
        localStorage.setItem('items', JSON.stringify(storedItems));

    }
    else
    {
      var i=0;
      for(i=0;i<storedItems.length;i++)
      {
        if(storedItems[i].id===event.target.value)
        {
          storedItems[i].count++;
          break;
        }
      }

    }
    
    
    console.log(storedItems)
    event.preventDefault()
    

    
    
  
  }
 
  return (
    <div className="card" style={{width: "18rem"}}>
    {/* <img src="..." className="card-img-top" alt="..."/> */}
  <div className="card-body">
    <h5 className="card-title">{props.title}</h5>
    {/* <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p> */}
  </div>
    <button type="button" className="btn btn-primary" onClick={handleClick} value={props.id}>ADD</button>
    </div>
  );
}

export default Card;
