import React,{useReducer,useContext,useState} from 'react'
import { AppContext } from './context';
function ConfirmationCard(props) {
  const storedItems = JSON.parse(localStorage.getItem('items'))

  const [product,setProduct]=useState(props);
 
  function handleClick(event)
  {
    var name=event.target.name;
    var val=event.target.value;
    if(name==="Add")
    {
      var i;
      for(i=0;i<storedItems.length;i++)
      {
        if(storedItems[i].id===val)
        {
          break;
        }
      }
      storedItems[i].count++;
      localStorage.setItem('items', JSON.stringify(storedItems));
    }
    else
    {
      var i;
      for(i=0;i<storedItems.length;i++)
      {
        if(storedItems[i].id===val)
        {
          break;
        }
      }
      if(storedItems[i].count>1)
      {
        storedItems[i].count++;
        localStorage.setItem('items', JSON.stringify(storedItems));
      }
      else
      {
        storedItems.splice(i, 1);
        localStorage.setItem('items', JSON.stringify(storedItems));
      }
      console.log("Remove Item");
    }

  }
    
  return (
    <div>
    <ul className="list-group">
    <li className="list-group-item d-flex justify-content-between align-items-center">
    {product.id}
    
    <button type="button" className="btn btn-primary btn-sm" onClick={handleClick} name="Add" value={product.id}>+</button>
    <button type="button" className="btn btn-primary btn-sm" onClick={handleClick} name="Remove"value={product.id}>-</button>
    <span className="badge bg-primary rounded-pill">{product.count}</span>
    <span className="badge bg-primary rounded-pill">Price</span>
    </li>
    </ul>
    </div>
    
  );
}

export default ConfirmationCard;
