import React,{useState,useEffect} from 'react'
import Navbar from './Nav';
import Home from './Home';
import Confirmation from './Confirmation';
import Order from './Order';
import {BrowserRouter as Router,Route,Routes} from "react-router-dom"
import { AppContext } from './context';

function App() {

  const [order,setOrder]=useState(["first element","Second element"]);
  const [items, setItems] = useState([]);

  // useEffect(() => {
  //   console.log("changing")
  //   const storedItems = JSON.parse(localStorage.getItem('items')) ;
  //   setItems(storedItems);
  // }, []);

  useEffect(() => {
    localStorage.setItem('items', JSON.stringify(items));
  }, [items]);

    function addOrder(id)
    {

        let temp=order;
        temp.push(id)

        setOrder(temp)
        console.log("Add clicked");
        console.log(order);
    }

  return (
    <Router>
      <AppContext.Provider value={{order,setOrder,addOrder}}>
      <Navbar/>

      <Routes>

        <Route path="/"element={<Home setOrder={setOrder} addOrder={addOrder}/>}/>
        <Route path="/confirmation"element={<Confirmation order={order} addOrder={addOrder}/>}/>
        <Route path="/orders"element={<Order/>}/>

      </Routes>
      </AppContext.Provider>
    </Router>
  );
}

export default App;
