import React,{useState,useContext} from 'react'
import Navbar from './Nav';
import { AppContext } from './context';
import ConfirmationCard from './ConfirmationCard';
function Confirmation({order,addOrder}) {
  // const data=useGlobalContext();
  
   console.log("confirmation")
   const storedItems = JSON.parse(localStorage.getItem('items'))
  //  let products=storedItems;
   const[products,setProducts]=useState(storedItems);
   console.log(storedItems)
   function changeList(id,command)
   {
      if(command==="Add")
      {
        products[id].count++;
      }
      else
      {
        if(products[id].count===1)
        {
          products.slice(id,1);
        }
        else
        {
          products[id].count--;
        }
      }
      setProducts(products);
      console.log(id,command);

   }
  
   

  return (
    <div className="App">

      <header className="App-header">
      </header>
      {products.map((product) => (
        <ConfirmationCard key={product.id} id={product.id} count={product.count} changeList={changeList}/>
      ))}
    </div>
  );
}

export default Confirmation;
