import React,{useReducer,useContext} from 'react'
import { AppContext } from './context';
function ConfirmationCard(props) {
  function handleClick(event)
  {
    var name=event.target.name;
    if(name==="Add")
    {
      console.log("Add Iterm");
    }
    else
    {
      console.log("Remove Item");
    }

  }
    
  return (
    <div>
    <ul className="list-group">
    <li className="list-group-item d-flex justify-content-between align-items-center">
    {props.id}
    
    <button type="button" className="btn btn-primary btn-sm" onClick={handleClick} name="Add">+</button>
    <button type="button" className="btn btn-primary btn-sm" onClick={handleClick} name="Remove">-</button>
    <span className="badge bg-primary rounded-pill">{props.count}</span>
    <span className="badge bg-primary rounded-pill">Price</span>
    </li>
    </ul>
    </div>
    
  );
}

export default ConfirmationCard;
